import React from 'react';
import { View, StyleSheet } from 'react-native';

// Récupérer les éléments du DOM
const searchForm = document.getElementById("search-form") as HTMLFormElement;
const locationInput = document.getElementById("location-input") as HTMLInputElement;

// Événement de soumission du formulaire
searchForm.addEventListener("submit", (event) => {
  event.preventDefault();
  const location = locationInput.value;
  getWeatherData(location);
  const name: string = "John Doe";
const age: number = 30;

// Affichage des variables à la console
console.log("Nom :", name);
console.log("Âge :", age);

// Affichage d'un message de débogage
console.log("Ceci est un message de débogage.");

// Affichage d'une expression
const sum = 5 + 10;
console.log("Somme :", sum);
});

// Fonction pour obtenir les données météo en utilisant la localisation de l'utilisateur
function getWeatherData(location: string) {
  // Si l'utilisateur a entré une localisation, utiliser cette localisation pour obtenir les données météo
  if (location.trim() !== "") {
    // Code pour récupérer les données météo en utilisant l'API météo avec la localisation entrée par l'utilisateur
    // Vous pouvez utiliser des requêtes HTTP ou des bibliothèques pour cela.
    console.log(`Obtenir les données météo pour : ${location}`);
  } else {
    // Si l'utilisateur n'a pas entré de localisation, essayer de récupérer la localisation de l'utilisateur via l'API de géolocalisation du navigateur
    if (navigator.geolocation) {
      navigator.geolocation.getCurrentPosition(
        (position) => {
          const latitude = position.coords.latitude;
          const longitude = position.coords.longitude;
          console.log(`Latitude : ${latitude}, Longitude : ${longitude}`);

          // Utilisez les coordonnées géographiques (latitude et longitude) pour obtenir les données météo
          // Vous pouvez utiliser une API météo qui prend en charge la récupération des données météo en utilisant les coordonnées géographiques.
          // Exemple : getWeatherDataByCoords(latitude, longitude);
        },
        (error) => {
          console.log("Erreur lors de la récupération de la localisation :", error.message);
        }
      );
    } else {
      console.log("La géolocalisation n'est pas prise en charge par ce navigateur.");
    }
  }
}


export default App;
